    m, n = len(s1), len(s2)
    dp = [[0] * (m + 1) for _ in range(n + 1)]

    for j in range(0, m + 1):
        dp[0][j] = j

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            if s1[j - 1] == s2[i - 1]:
                dp[i][j] = dp[i - 1][j - 1]
            else:
                dp[i][j] = dp[i][j - 1]

